mapper.insert:
INSERT INTO tradItem(	
	tditTitle
	,tditSubTitle
	,tditPublisherCd
	,tditPublishingDate
	,tditPrice
	,tditDiscountCd
	,tditDiscountRate
	,tditStateCd 
	,tditItemCate
	,tditItemCate2
	,tditIsbn
	,tditPage
	,tditSize
	,tditWeight
	,tditBookDesc
	,tditBookDesc2
	,tditTableOfContents
	,tditDelNy
	,regDateTime
	,modDateTime
)VALUES(
	#{tditTitle}
	,#{tditSubTitle}
	,#{tditPublisherCd}

		,NULL
		,#{tditPublishingDate}

	,#{tditPrice}
	,#{tditDiscountCd}
	,#{tditDiscountRate}
	,#{tditStateCd}
	,#{tditItemCate}
	,#{tditItemCate2}
	,#{tditIsbn}
	,#{tditPage}
	,#{tditSize}
	,#{tditWeight}
	,#{tditBookDesc}
	,#{tditBookDesc2}
	,#{tditTableOfContents}
	,0
	,#{regDateTime}
	,#{modDateTime}
)

	SELECT last_insert_id()

mapper.update:
UPDATE tradItem SET 
	tditPrice = #{tditPrice}
	,tditDiscountCd = #{tditDiscountCd}
	,tditDiscountRate = #{tditDiscountRate}
	,tditStateCd = #{tditStateCd}
	,tditItemCate = #{tditItemCate}
	,tditItemCate2 = #{tditItemCate2}
	,tditSize=#{tditSize}
	,tditPage = #{tditPage}
	,tditWeight = #{tditWeight}
	,tditBookDesc = #{tditBookDesc}
	,tditBookDesc2 = #{tditBookDesc2}
	,tditTableOfContents = #{tditTableOfContents}
	,modDateTime = #{modDateTime}
WHERE 1=1
	AND tditSeq=#{tditSeq}

UPDATE ${tableName} SET
	type = #{type}
	,defaultNy = #{defaultNy} 
	,sort = #{sort}
	,originalName = #{originalName}
	,uuidName = #{uuidName}
	,ext = #{ext}
	,size = #{size}
	,path = #{path}
	,delNy = #{delNy}
WHERE 1=1
	AND pseq = #{pseq}
	AND type = #{type}

UPDATE tradItemKeyword SET 
	tdkwKeyword = #{tdkwKeyword}
	,tdkwDefaultNy = #{tdkwDefaultNy}
	,tdkwOrder = #{tdkwOrder}
WHERE 1=1
	AND tditSeq=#{tditSeq}
	AND tdkwOrder = #{tdkwOrder}
